CREATE TABLE `bs_booking` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `customer_id` int(11) DEFAULT '0' COMMENT '客户信息编号',
  `code` varchar(50) DEFAULT NULL COMMENT '业务编码',
  `is_del` tinyint(255) DEFAULT '1' COMMENT '1：正常 0:已经删除',
  `create_time` int(255) DEFAULT '0' COMMENT '创建时间 时间戳',
  `update_time` int(11) DEFAULT '0' COMMENT '最后修改时间 时间戳',
  `creator_id` int(11) DEFAULT NULL COMMENT '创建人用户编号',
  `consultation_way_id` int(11) DEFAULT NULL COMMENT '咨询方式',
  `department_id` int(11) DEFAULT '0' COMMENT '科室编号',
  `disease_id` int(11) DEFAULT NULL COMMENT '病种编号',
  `source_id` int(11) DEFAULT NULL COMMENT '来源编号',
  `custom_service_id` int(11) DEFAULT NULL COMMENT '咨询客服用户编号',
  `consultation_time` datetime DEFAULT NULL COMMENT '咨询时间，什么时候和客服联系的',
  `yy_time` datetime DEFAULT NULL COMMENT '预约日期',
  `yy_doctor_id` int(11) DEFAULT NULL COMMENT '预约医生编号',
  `yy_code` varchar(50) DEFAULT NULL COMMENT '预约号',
  `yy_remark` varchar(255) DEFAULT NULL COMMENT '预约备注',
  `chat_record` text COMMENT '聊天记录',
  `chat_file` varchar(255) DEFAULT NULL COMMENT '聊天文件',
  `source_site_id` int(11) DEFAULT '0' COMMENT '来源站点编号',
  `source_ip` varchar(50) DEFAULT NULL COMMENT '来源IP',
  `source_entry` varchar(255) DEFAULT NULL COMMENT '来源入口',
  `source_consulting_page` varchar(255) DEFAULT NULL COMMENT '咨询页面',
  `source_search_keywords` varchar(255) DEFAULT NULL COMMENT '搜索关键词',
  `source_matching_keywords` varchar(255) DEFAULT NULL COMMENT '匹配关键词',
  `jz_doctor_id` int(4) DEFAULT '0' COMMENT '就诊医生',
  `jz_time` int(11) DEFAULT NULL COMMENT '就诊时间',
  `jz_guide_doctor_id` int(11) DEFAULT NULL COMMENT '接待导医',
  `status` int(11) DEFAULT '1' COMMENT '状态',
