  `city_id` int(11) DEFAULT '0' COMMENT '市编号',
  `swt` text COMMENT '商务通内容',
  `consultation_remark` varchar(255) DEFAULT NULL COMMENT '咨询备注',
  `doctor_remark` varchar(255) DEFAULT NULL COMMENT '医生备注',
  `remark` varchar(255) DEFAULT NULL COMMENT '其他备注',
  `disease_desc` text COMMENT '病情描述',
  `input_ip` varchar(50) DEFAULT NULL,
