  `appointment_mode_id` int(11) DEFAULT NULL COMMENT '预约方式',
  PRIMARY KEY (`id`),
  UNIQUE KEY `code` (`code`)
) ENGINE=MyISAM AUTO_INCREMENT=6906 DEFAULT CHARSET=gbk COMMENT='预约信息表';
